def Parse_default():    inputAddress = r"C:\Users\Dell\OneDrive\Desktop\IT's MOrbin' time\IKT_17starplatinum\I\I semester\lab4\schedule.xml"    outputAddress = r"C:\Users\Dell\OneDrive\Desktop\IT's MOrbin' time\IKT_17starplatinum\I\I semester\lab4\schedule.json"    inputFile = open(inputAddress, 'r', encoding='utf-8')    outputFile = open(outputAddress, 'w', encoding='utf-8')    outputFile.write('{\n')    inputFile.readline()    count = 0    lineDel = inputFile.read().split("\n")    for j in range(len(lineDel)):        lineSh = lineDel[j].strip().replace('<', '>').split('>')[1:-1]        if j == len(lineDel) - 1 or lineDel[j + 1].strip()[:2] == '</':            token = ''        else:            token = ','        if len(lineSh) > 2:            lineSh = lineSh[:-1]        if len(lineSh) == 1:            if lineSh[0][0] == '/':                outputFile.write('      ' * (count) + '}' + token + '\n')                count -= 1            else:                outputFile.write('    ' * (count+1) + '"' + lineSh[0] + '": {\n')                count += 1        else:            outputFile.write('    ' * (count+1) + '"' + lineSh[0] + '" : "' + lineSh[1] + '"' + token + '\n')    outputFile.write('}')    outputFile.close()    inputFile.close()Parse_default()